import { useContext } from "react";
import { AppContext } from "../../context/GithubContext";
import './css/style.css'

function Profile() {
    const { Users, dispatch } = useContext(AppContext);
    const [imageLoading, setImageLoading] = useState(true);

    if (!Users) {
        return <div>Loading...</div>;
    }

    // Destructure user data
    const {
        login,
        avatar_url,
        bio,
        name,
        type,
        company,
        location,
        followers,
        following,
        public_repos,
        public_gists,
        html_url,
        organizations_url,
        repos_url,
    } = Users;

        // Function to render a loading div if data is not available
        const renderLoading = (value) => {
            return value ? value : <div>Loading...</div>;
        };

    return (
        <>
            <div className="profile">
                <div className="profile_items">
                    <div className="profile_pic">
                    {imageLoading && <div>Loading...</div>}
                        <img 
                        src={avatar_url}
                        alt={login}
                        onload />
                    </div>
                    <div className="profile_details">
                        <a href={html_url}><h2>{login}</h2></a>
                        <p><strong>Bio:</strong> {bio}</p>
                        <p><strong>Type:</strong> {type}</p>
                        <p><strong>Company:</strong> {company || 'N/A'}</p>
                        <p><strong>Location:</strong> {location || 'N/A'}</p>
                        <p><strong>Followers:</strong> {followers}</p>
                        <p><strong>Following:</strong> {following}</p>
                        <p><strong>Public Repositories:</strong> {public_repos}</p>
                        <p><strong>Public Gists:</strong> {public_gists}</p>
                        <a href={repos_url}>View Repositories</a>
                    </div>
                </div>
            </div>



        </>

    )

}

export default Profile
